AND T.BUYER_ID = #{buyerId}

ORDER BY T.TRAN_NO DESC
) inner_table
WHERE ROWNUM <=
#{search.endRowNum}
)
WHERE row_seq >= #{search.startRowNum}

SELECT * FROM (
SELECT inner_table.*, ROWNUM AS row_seq FROM (
SELECT


