mapper.update:
UPDATE TRANSACTION SET PAYMENT_OPTION = #{paymentOption}, RECEIVER_NAME = #{receiverName}, RECEIVER_PHONE = #{receiverPhone}, DEMAILADDR = #{divyAddr}, DLVY_REQUEST = #{divyRequest}, DLVY_DATE =
TO_DATE(#{divyDate}, 'YYYY-MM-DD') NULL
WHERE TRAN_NO = #{tranNo}

UPDATE TRANSACTION
SET
TRAN_STATUS_CODE = #{tranCode}
WHERE TRAN_NO = #{tranNo}

UPDATE
TRANSACTION
SET TRAN_STATUS_CODE = #{tranCode}
WHERE PROD_NO = #{prodNo}

mapper.select:
SELECT prodNo, tranCode FROM ( SELECT t.PROD_NO AS prodNo, t.TRAN_STATUS_CODE AS tranCode, ROW_NUMBER() OVER (PARTITION BY t.PROD_NO ORDER BY t.TRAN_NO DESC) AS rn FROM TRANSACTION t
t.PROD_NO IN
#{value}
AND t.TRAN_STATUS_CODE != '004'
) WHERE rn = 1

SELECT prodNo, user_id AS buyerId, TO_CHAR(order_date, 'YYYY-MM-DD') AS orderDate, tran_status_code AS tranCode FROM ( SELECT t.PROD_NO AS prodNo, t.USER_ID, t.ORDER_DATE, t.TRAN_STATUS_CODE, ROW_NUMBER() OVER (PARTITION BY t.PROD_NO ORDER BY t.TRAN_NO DESC) AS rn FROM TRANSACTION t WHERE t.PROD_NO IN
#{value}
) WHERE rn = 1
