mapper.update:
UPDATE USERS

	user_name = #{userName},
	cell_phone = #{phone},
	addr = #{addr},
	email = #{email}

WHERE user_id = #{userId}

mapper.select:
SELECT *
FROM (
SELECT inner_table.*, ROWNUM AS row_seq
FROM (
SELECT user_id, user_name, email
FROM USERS

	
		
			user_id = #{searchKeyword}
		
		
			user_name = #{searchKeyword}
		
	

ORDER BY user_id
) inner_table
WHERE ROWNUM  <= 
#{endRowNum}
)
WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT COUNT(*)
FROM (
SELECT user_id, user_name, email
FROM USERS

	
		
			user_id = #{searchKeyword}
		
		
			user_name = #{searchKeyword}
		
	

)

SELECT COUNT(*)
FROM USERS
WHERE EMAIL = #{email}

	AND USER_ID != #{excludeUserId}

SELECT COUNT(*)
FROM USERS
WHERE CELL_PHONE = #{phone}

	AND USER_ID != #{excludeUserId}
